mapper.select:
SELECT *
FROM payments p
LEFT JOIN orders o
ON p.`ORDERS_ID` = o.`ID`
WHERE p.`CREATED_AT` >= #{startDay}
    AND p.`CREATED_AT` <= #{endDay}
    AND payment_method = #{type}
    AND o.`TOTAL_PRICE` = #{price}
ORDER BY p.created_at desc;

mapper.update:
UPDATE payments
   SET orders_id = ${ordersId}
      ,payment_method = '${paymentMethod}'
      ,status = '${status}'
      ,updated_at = now()
WHERE id = #{id}
