mapper.update:
UPDATE product
   SET name = #{name}
      ,category = #{category}
      ,description = #{description}
      ,content = #{content}
      ,price = #{price}
      ,stock_check = #{stockCheck}
      ,stock = #{stock}
      ,seq = #{seq}
      ,updated_at = now()
WHERE id = #{id}

mapper.delete:
DELETE product
FROM product
WHERE id = #{id}
